screen.userinput:
cat > /etc/rc.d/init.d/portmap << "EOF" #!/bin/sh # Begin /etc/rc.d/init.d/portmap
. /etc/rc.d/init.d/functions
case "$1" in start) echo "Starting RPC Portmap" loadproc /sbin/portmap ;;
stop) echo "Stopping Portmap" killproc /sbin/portmap ;;
restart) $0 stop /bin/sleep 1 $0 start ;;
*) echo "Usage: $0 {start|stop|restart}" exit 1 ;;
esac
# End /etc/rc.d/init.d/portmap EOF chmod 754 /etc/rc.d/init.d/portmap && cd /etc/rc.d/init.d && ln -sf ../init.d/portmap ../rc0.d/K49portmap && ln -sf ../init.d/portmap ../rc1.d/K49portmap && ln -sf ../init.d/portmap ../rc2.d/K49portmap && ln -sf ../init.d/portmap ../rc3.d/S22portmap && ln -sf ../init.d/portmap ../rc4.d/S22portmap && ln -sf ../init.d/portmap ../rc5.d/S22portmap && ln -sf ../init.d/portmap ../rc6.d/K49portmap
