userinput.command:
cat > /etc/rc.d/init.d/mysql << "EOF" #!/bin/bash # Begin $rc_base/init.d/
# Based on sysklogd script from LFS-3.1 and earlier. # Rewritten by Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Starting MySQL daemon..." /usr/bin/mysqld_safe --user=mysql 2>&1 >/dev/null & evaluate_retval ;;
stop) echo "Stopping MySQL daemon..." killproc mysqld ;;
restart) $0 stop sleep 1 $0 start ;;
status) statusproc /usr/sbin/mysqld ;;
*) echo "Usage: $0 {start|stop|restart|status}" exit 1 ;; esac
# End $rc_base/init.d/ EOF chmod 755 /etc/rc.d/init.d/mysql
