screen.userinput:
cat > /etc/rc.d/init.d/rsyncd << "EOF" #!/bin/sh # Begin $rc_base/init.d/rsyncd
# Based on sysklogd script from LFS-3.1 and earlier. # Rewritten by Gerard Beekmans - gerard@linuxfromscratch.org
source /etc/sysconfig/rc source $rc_functions
case "$1" in start) echo "Starting RSYNC Server..." loadproc /usr/bin/rsync --daemon --compress ;;
stop) echo "Stopping RSYNC Server..." killproc /usr/bin/rsync ;;
reload) echo "Reloading RSYNC Server..." reloadproc /usr/bin/rsync ;;
restart) $0 stop sleep 1 $0 start ;;
status) statusproc /usr/bin/rsync ;;
*) echo "Usage: $0 {start|stop|reload|restart|status}" exit 1 ;; esac
# End $rc_base/init.d/rsyncd EOF chmod 755 /etc/rc.d/init.d/rsyncd
